import { iPregunta } from "./Q&A"
const clipboardy = require('clipboardy');

const createQA = (pregunta: string, opcionA: string, opcionB: string, opcionC: string, opcionD: string, respuesta: "A" | "B" | "C" | "D", imagen: string) => {
    pregunta = grammar(pregunta)

    opcionA = grammar(opcionA)
    opcionB = grammar(opcionB)
    opcionC = grammar(opcionC)
    opcionD = grammar(opcionD)


    const result: string = `{
        p: '¿${pregunta}?',
        o: {
            A: '${opcionA}',
            B: '${opcionB}',
            C: '${opcionC}',
            D: '${opcionD}',
        },
        x: '${respuesta}',
        i: '${imagen}'
    },`


    return result
}

function grammar(pregunta: string): string {
    let que = "qué ", como = "cómo ", cuando = "cuándo ", cuantas = "cuántas ", porQue = "porqué ", conQue = "con qué ", cual = "cuál "
    pregunta = pregunta.replace(/que/ig, que)
    pregunta = pregunta.replace(/como/ig, como)
    pregunta = pregunta.replace(/cuando/ig, cuando)
    pregunta = pregunta.replace(/cuantas/ig, cuantas)
    pregunta = pregunta.replace(/por que/ig, porQue)
    pregunta = pregunta.replace(/con que/ig, conQue)
    pregunta = pregunta.replace(/cual/ig, cual)

    pregunta = pregunta.replace(/  /ig, " ")
    pregunta = pregunta.replace(/   /ig, " ")
    pregunta = pregunta.replace(/    /ig, " ")

    pregunta = upperTopics(pregunta)

    pregunta = upperCase(pregunta)
    return pregunta
}

function upperTopics(topic: string): string {
    let Infarto = "Infarto", Acv = "Acv", Heimlich = "Heimlich", Rcp = "Rcp", bebes = "bebés", bebe = "bebé"
    topic = topic.replace(/infarto/ig, Infarto)
    topic = topic.replace(/acv/ig, Acv)
    topic = topic.replace(/heimlich/ig, Heimlich)
    topic = topic.replace(/rcp/ig, Rcp)
    topic = topic.replace(/bebes/ig, bebes)
    topic = topic.replace(/bebe/ig, bebe)

    return topic
}

function upperCase(word: string): string {
    return word[0].toUpperCase() + word.slice(1)
}

const QA = createQA(
    "que tipo de acv se parece mas a un infarto",

    "el acv hemorragico, porque ocurre en un vaso",
    "los dos acv son parecidos a un infarto",
    "el acv isquémico ya que tapa un vaso pero no se rompe",
    "ningun acv se parece en nada a un infarto",

    "A",
    `data:image/jpeg;base64,`,

)

clipboardy.writeSync(QA);


